css:
.scrollbars-visible-always .settings-view ::-webkit-scrollbar {
  width: 8px;
}
.scrollbars-visible-always .settings-view ::-webkit-scrollbar-track {
  -webkit-box-shadow: none;
  background: #D7D7D7 !important;
}
.scrollbars-visible-always .settings-view ::-webkit-scrollbar-thumb {
  -webkit-border-radius: 10px;
  border-radius: 10px;
  background: #B5B5B5;
  -webkit-box-shadow: none !important;
}
.scrollbars-visible-always .settings-view ::-webkit-scrollbar-thumb:window-inactive {
  background: #B5B5B5;
}
.settings-view {
  display: -webkit-flex;
}
.settings-view .btn-default,
.settings-view .btn-group > .btn-default {
  background: #fff;
  color: #333333;
  border: 1px solid #ccc;
}
.settings-view .btn-default:hover,
.settings-view .btn-group > .btn-default:hover {
  color: #333333;
  background: #ddd;
}
.settings-view .btn {
  box-shadow: none;
}
.settings-view .text {
  color: #333333;
}
.settings-view .error-message .alert {
  margin-bottom: 0;
}
.settings-view .error-message .error-details {
  background: #F7F7F9;
  border: 1px solid #E1E1E8;
}
.settings-view .error-message .error-link {
  margin-left: 5px;
}
.settings-view .controls select.form-control {
  margin-bottom: 10px;
}
.settings-view .editor-container {
  margin-bottom: 10px;
}
.settings-view .editor-container .editor.mini,
.settings-view .editor-container .editor.mini::shadow {
  opacity: .75;
}
.settings-view .editor-container .editor.mini .selection .region,
.settings-view .editor-container .editor.mini::shadow .selection .region {
  background-color: rgba(0, 0, 0, 0.2);
}
.settings-view .editor-container .editor.mini.is-focused,
.settings-view .editor-container .editor.mini.is-focused::shadow {
  opacity: 1;
}
.settings-view .editor-container .editor.mini.is-focused .selection .region,
.settings-view .editor-container .editor.mini.is-focused::shadow .selection .region {
  background-color: #99d6ff;
}
.settings-view .editor-container .editor.mini,
.settings-view .editor-container .editor.mini::shadow,
.settings-view .editor-container .editor.mini.is-focused,
.settings-view .editor-container .editor.mini.is-focused::shadow {
  color: #333333;
  background-color: #fff;
}
.settings-view .editor-container .editor.mini .cursor,
.settings-view .editor-container .editor.mini::shadow .cursor,
.settings-view .editor-container .editor.mini.is-focused .cursor,
.settings-view .editor-container .editor.mini.is-focused::shadow .cursor {
  border-color: #000;
}
.settings-view .editor-container .editor.mini .placeholder-text,
.settings-view .editor-container .editor.mini::shadow .placeholder-text,
.settings-view .editor-container .editor.mini.is-focused .placeholder-text,
.settings-view .editor-container .editor.mini.is-focused::shadow .placeholder-text {
  color: #888;
}
.settings-view .section {
  max-width: 1024px;
}
.settings-view section .section-heading:first-child,
.settings-view .section .section-heading:first-child {
  padding-top: 10px;
  padding-bottom: 10px;
  border-bottom: 1px solid #858585;
  color: #262626;
  font-size: 16px;
  margin: 10px 0;
  -webkit-user-select: none;
  cursor: default;
}
.settings-view .control-label {
  -webkit-user-select: none;
  cursor: default;
}
.settings-view .setting-title {
  -webkit-user-select: none;
}
.settings-view .setting-description {
  color: #999;
  font-size: 10px;
  -webkit-user-select: none;
}
.settings-view .setting-description:empty {
  display: none;
}
.settings-view .link {
  color: #428bca;
}
.settings-view .link:hover,
.settings-view .link:focus {
  color: #2a6496;
}
.settings-view .config-menu {
  display: -webkit-flex;
  -webkit-flex-direction: column;
  box-shadow: -1px 0px 1px -1px #aeaeae inset;
  background-color: #e7e7e7;
  -webkit-user-select: none;
  padding-top: 10px;
}
.settings-view .config-menu .icon:before {
  text-align: center;
}
.settings-view .config-menu .panels-menu .panel-menu-separator {
  border-bottom: 1px solid #ccc;
  margin: 10px;
}
.settings-view .config-menu .panels-menu .settings-filter {
  margin-left: 10px;
  margin-right: 10px;
}
.settings-view .config-menu .panels-packages {
  -webkit-flex-grow: 1;
  overflow: auto;
  height: 0;
}
.settings-view .config-menu .panels-packages li + li {
  margin-top: 0;
}
.settings-view .config-menu .panels-packages li > a {
  border-radius: 1px !important;
  padding: 12px 14px;
  font-size: 12px;
  font-weight: bold;
  line-height: 16px;
  opacity: .85;
  transition: opacity 0.1s ease-in;
  box-shadow: inset 0 -1px 0 rgba(0, 0, 0, 0.07);
}
.settings-view .config-menu .panels-packages li > a:hover {
  opacity: 1;
  box-shadow: inset 0 -1px 0 rgba(0, 0, 0, 0.07) !important;
}
.settings-view .config-menu .panels-packages li:first-child {
  margin-top: 2px;
}
.settings-view .config-menu .panels-packages li:last-child {
  margin-bottom: 10px;
}
.settings-view .config-menu .panels-packages li.active a,
.settings-view .config-menu .panels-packages li.active a:hover {
  opacity: 1;
  box-shadow: 0 0 0 1px #225cc4 !important;
}
.settings-view .config-menu .panels-packages li.active a .package-author,
.settings-view .config-menu .panels-packages li.active a:hover .package-author,
.settings-view .config-menu .panels-packages li.active a .package-version,
.settings-view .config-menu .panels-packages li.active a:hover .package-version {
  color: #fff;
  opacity: .6;
}
.settings-view .config-menu .panels-packages .package-version {
  font-size: 11px;
  line-height: 16px;
  margin-left: 5px;
  font-weight: normal;
  color: #999;
}
.settings-view .config-menu .panels-packages .package-author {
  font-size: 11px;
  font-weight: normal;
  display: block;
  color: #999;
}
.settings-view .config-menu .button-area {
  margin: 10px auto;
}
.settings-view .config-menu .nav > li > a {
  border-radius: 0;
  color: #434343;
  white-space: nowrap;
}
.settings-view .config-menu .nav > li > a:hover {
  background-color: #f2f2f2;
  box-shadow: -1px 0px 1px -1px #aeaeae inset;
}
.settings-view .config-menu .nav > li.active > a {
  background-color: #3c76dd;
  color: #f0f0f0;
}
.settings-view .panels {
  -webkit-flex: 1;
  -webkit-flex-flow: column;
  display: -webkit-flex;
  position: relative;
  overflow-y: auto;
  padding: 20px;
  background-color: #f2f2f2;
}
.settings-view .panels > div {
  -webkit-flex: 1;
}
.settings-view .settings-panel {
  padding-bottom: 10px;
}
.settings-view .settings-panel .section-body {
  padding-top: 5px;
}
.settings-view .settings-panel label {
  color: #333333;
  font-size: 13px;
}
.settings-view .settings-panel .grammar-filetypes {
  margin-top: 10px;
}
.settings-view .installed-package-view .package-keymap-table,
.settings-view .installed-package-view .package-grammars-table,
.settings-view .installed-package-view .package-snippets-table {
  padding-top: 10px;
  padding-left: 10px;
}
.settings-view .installed-package-view .package-keymap-table td,
.settings-view .installed-package-view .package-grammars-table td,
.settings-view .installed-package-view .package-snippets-table td {
  white-space: nowrap;
}
.settings-view .installed-package-view .package-keymap-table .snippet-body,
.settings-view .installed-package-view .package-grammars-table .snippet-body,
.settings-view .installed-package-view .package-snippets-table .snippet-body,
.settings-view .installed-package-view .package-keymap-table .snippet-prefix,
.settings-view .installed-package-view .package-grammars-table .snippet-prefix,
.settings-view .installed-package-view .package-snippets-table .snippet-prefix {
  font-family: monospace;
}
.settings-view .installed-package-view .update-message {
  padding-right: 10px;
}
.settings-view .installed-package-view .update-link:hover,
.settings-view .installed-package-view .repo-link:hover {
  cursor: pointer;
  text-decoration: underline;
}
.settings-view .packages {
  height: 100%;
  display: -webkit-flex;
  -webkit-flex-flow: column;
}
.settings-view .packages .themes-label {
  font-weight: bold;
  font-size: 14px;
}
.settings-view .packages .theme-chooser {
  padding-top: 20px;
}
.settings-view .packages .theme-description {
  margin-top: 5px;
  padding-bottom: 10px;
  -webkit-user-select: none;
  cursor: default;
}
.settings-view .packages .package-container {
  padding-top: 10px;
  max-width: 100%;
}
.settings-view .packages .search-message,
.settings-view .packages .featured-message {
  margin: 10px 10px 0 10px;
}
.settings-view .packages .error-message {
  padding: 10px 10px 0 10px;
}
.settings-view .packages .editor-container {
  margin-bottom: 0;
  padding-bottom: 0;
}
.settings-view .packages .available-package-view {
  border-radius: 1px;
  padding-left: 10px;
  padding-right: 10px;
}
.settings-view .packages .available-package-view.col-lg-4 {
  min-width: 190px;
  padding: 1% 0;
  padding-left: 10px;
  padding-right: 10px;
}
.settings-view .packages .available-package-view .thumbnail {
  border-radius: 3px;
  margin: 0;
  min-height: 140px;
}
.settings-view .packages .available-package-view .thumbnail .caption {
  width: 100%;
  position: relative;
}
.settings-view .packages .available-package-view .package-name {
  text-overflow: ellipsis;
  white-space: nowrap;
  width: 100%;
  overflow: hidden;
  line-height: 20px;
  margin: 5px 0;
}
.settings-view .packages .available-package-view .description {
  font-size: 12px;
  line-height: 18px;
  color: #999;
  overflow: hidden;
  min-height: 38px;
  max-height: 38px;
  text-overflow: ellipsis;
  display: -webkit-box;
  -webkit-line-clamp: 2;
  /* number of lines to show */
  -webkit-box-orient: vertical;
}
.settings-view .packages .available-package-view .downloads {
  font-size: 12px;
  line-height: 12px;
  color: #999;
  overflow: hidden;
  text-overflow: ellipsis;
  display: -webkit-box;
  -webkit-line-clamp: 1;
  /* number of lines to show */
  -webkit-box-orient: vertical;
}
.settings-view .packages .available-package-view .package-status {
  position: absolute;
  right: 0px;
  top: 5px;
  text-align: center;
}
.settings-view .packages .available-package-view .package-status.icon-cloud-download {
  color: #428bca;
}
.settings-view .packages .available-package-view .package-status.icon-check {
  color: #6CC644;
}
.settings-view .packages .available-package-view .package-status.icon-alert {
  color: #BD2C00;
}
.settings-view .btn-retry {
  margin-left: 10px;
}
.settings-view .readme {
  margin-top: 10px;
}
.settings-view .panel-heading .label {
  margin-left: 10px;
}
.settings-view .loading-area span:before {
  font-size: 1.1em;
  width: 1.1em;
  height: 1.1em;
}
.settings-view .error-view {
  margin: 10px 0 10px 0;
}
.settings-view .packages-panel .nav .badge {
  margin-left: 5px;
}
.settings-view .copy-icon {
  cursor: pointer;
  padding-right: 2px;
}
.settings-view .copy-icon:hover {
  color: #2a6496;
}
.settings-view .keybinding-panel table {
  width: 100%;
  table-layout: initial;
}
.settings-view .keybinding-panel th {
  padding: 8px 10px;
  padding-left: 0;
}
.settings-view .keybinding-panel td {
  height: 25px;
}
.settings-view .keybinding-panel td,
.settings-view .keybinding-panel th {
  padding-left: 1%;
  padding-right: 1%;
  white-space: nowrap;
  overflow: hidden;
}
.settings-view .keybinding-panel col.keystroke {
  width: 13%;
}
.settings-view .keybinding-panel col.command {
  width: 38%;
}
.settings-view .keybinding-panel col.source {
  width: 8%;
}
.settings-view .keybinding-panel col.selector {
  width: 33%;
}
.settings-view .compile-tools-heading {
  font-weight: bold;
}
.settings-view .compile-tools-message {
  padding-top: 10px;
}
.settings-view .compile-tools-message code {
  text-shadow: none;
  border-width: 1px;
  border-style: solid;
}
@media all and (max-width: 800px) {
  .thumbnail .btn-toolbar .btn {
    width: 100%;
    margin: 2px 0;
  }
}
.available-updates-status {
  padding-left: 2px;
}
