css:
.settings-view .available-package-view {
  padding: 15px;
  margin-bottom: 10px;
  list-style-type: none;
  font-size: 13px;
  border-radius: 4px;
  border: 1px solid #9f9f9f;
  background-color: #ffffff;
  overflow: hidden;
}
.settings-view .available-package-view.disabled {
  background-color: #ffffff;
}
.settings-view .available-package-view.disabled .body,
.settings-view .available-package-view.disabled .avatar,
.settings-view .available-package-view.disabled .author,
.settings-view .available-package-view.disabled .stats {
  opacity: .5;
}
.settings-view .available-package-view.col-lg-4 {
  min-width: 190px;
  padding: 10px 0;
  padding-left: 10px;
  padding-right: 10px;
}
.settings-view .available-package-view .thumbnail {
  border-radius: 3px;
  margin: 0;
  min-height: 140px;
  color: #444444;
  border-radius: 4px;
  border: 1px solid #9f9f9f;
  background-color: #ffffff;
}
.settings-view .available-package-view .thumbnail .caption {
  width: 100%;
  position: relative;
}
.settings-view .available-package-view .package-name {
  text-overflow: ellipsis;
  white-space: nowrap;
  width: 100%;
  color: #000000;
  overflow: hidden;
  line-height: 20px;
  margin: 5px 0;
}
.settings-view .available-package-view .description {
  font-size: 12px;
  line-height: 18px;
  color: #444444;
  overflow: hidden;
  min-height: 38px;
  max-height: 38px;
  text-overflow: ellipsis;
  display: -webkit-box;
  -webkit-line-clamp: 2;
  /* number of lines to show */
  -webkit-box-orient: vertical;
}
.settings-view .available-package-view .downloads {
  font-size: 12px;
  line-height: 12px;
  color: #000000;
  overflow: hidden;
  text-overflow: ellipsis;
  display: -webkit-box;
  -webkit-line-clamp: 1;
  /* number of lines to show */
  -webkit-box-orient: vertical;
}
.settings-view .available-package-view .package-status {
  right: 0px;
  top: 5px;
  text-align: center;
}
.settings-view .available-package-view .package-status.icon-cloud-download {
  color: #428bca;
}
.settings-view .available-package-view .package-status.icon-check {
  color: #6CC644;
}
.settings-view .available-package-view .package-status.icon-alert {
  color: #BD2C00;
}
.settings-view .available-package-view .card-name {
  font-weight: 300;
  margin: 0 0 .2em 0;
  font-size: 1.2em;
  line-height: 1.4;
}
.settings-view .available-package-view .card-name .css-truncate-target {
  color: $greenDark;
}
.settings-view .available-package-view .stats .stats-item {
  margin-left: 15px;
  height: 26px;
  display: inline-block;
  line-height: 24px;
  vertical-align: top;
}
.settings-view .available-package-view .stats .stats-item .icon {
  color: #999999;
}
.settings-view .available-package-view .stats .star-box {
  display: inline-block;
  vertical-align: top;
}
.settings-view .available-package-view .stats .star-box a.social-count {
  font-size: 13px;
}
.settings-view .available-package-view .stats .star-box .star-button {
  padding: 0 6px;
}
.settings-view .available-package-view .stats .star-box .star-button .octicon {
  margin-right: 0px;
}
.settings-view .available-package-view .body .css-truncate-target {
  max-width: 100%;
}
.settings-view .available-package-view .package-description {
  white-space: normal;
  line-height: 1.4;
}
.settings-view .available-package-view .meta {
  display: flex;
  flex-wrap: wrap;
  justify-content: flex-end;
}
.settings-view .available-package-view .meta a.linked-octicon {
  text-decoration: none;
}
.settings-view .available-package-view .meta a.linked-octicon:hover .octicon {
  color: $greenDark;
}
.settings-view .available-package-view .meta a.linked-octicon:hover .value {
  color: $greenDark;
}
.settings-view .available-package-view .meta .action {
  border-radius: 3px;
  padding: 3px 6px;
}
.settings-view .available-package-view .meta .action:hover {
  background-color: #eaeaea;
}
.settings-view .available-package-view .meta .value {
  color: #999;
  font-size: 13px;
}
.settings-view .available-package-view .meta-user {
  flex: 1 0 auto;
  margin-top: 10px;
  margin-right: 10px;
}
.settings-view .available-package-view .meta-user .avatar {
  border-radius: 2px;
  height: 24px;
  width: 24px;
  display: inline-block;
  vertical-align: middle;
  background-color: rgba(0, 0, 0, 0.1);
}
.settings-view .available-package-view .meta-user .author {
  color: #999999;
  margin-left: 10px;
}
.settings-view .available-package-view .meta-user .author:focus {
  outline: none;
}
.settings-view .available-package-view .meta-controls {
  margin-top: 10px;
}
.settings-view .available-package-view .meta-controls .install-button.is-installing {
  opacity: 1;
  background-image: linear-gradient(to right, transparent 50%, rgba(0, 0, 0, 0.15) 50%);
  background-size: 10px 100%;
  -webkit-animation: available-package-is-installing 0.5s linear infinite;
}
.settings-view .available-package-view .meta-controls .status-indicator {
  padding: 0;
  width: 5px;
  pointer-events: none;
  transition: background .4s;
  background: #17ca65;
}
.settings-view .available-package-view .meta-controls .status-indicator.is-disabled {
  background: #ff4800;
}
.settings-view .available-package-view .meta-lower {
  padding: 13px;
  background-color: #ffffff;
  line-height: 1;
}
.settings-view .available-package-view.blank-slate {
  text-align: center;
  color: $beigeDark;
  height: 132px;
  line-height: 132px;
}
@-webkit-keyframes available-package-is-installing {
  100% {
    background-position: -10px 0px;
  }
}
