css:
atom-notifications {
  display: block;
  z-index: 1000;
  position: absolute;
  top: 0;
  right: 0;
  bottom: 0;
  padding: 10px;
  font-size: 1.2em;
  overflow-x: hidden;
  overflow-y: auto;
  pointer-events: none;
}
atom-notifications::-webkit-scrollbar {
  display: none;
}
atom-notifications atom-notification .close-all {
  display: none;
}
atom-notifications atom-notification:first-child .close-all {
  display: block;
}
atom-notifications atom-notification:first-child.has-close .message {
  padding-right: 115px;
}
atom-notifications atom-notification:only-child .close-all {
  display: none;
}
atom-notifications atom-notification:only-child.has-close .message {
  padding-right: 34px;
}
atom-notification {
  float: right;
  clear: right;
  position: relative;
  width: 450px;
  padding-left: 30px;
  max-height: 800px;
  margin-bottom: 2px;
  border-radius: 2px;
  word-wrap: break-word;
  pointer-events: auto;
}
atom-notification.icon:before {
  position: absolute;
  top: 0;
  left: 0;
  width: 30px;
  height: 100%;
  padding-top: 10px;
  text-align: center;
  border-radius: 2px 0 0 2px;
}
atom-notification .content {
  max-height: inherit;
  overflow-y: auto;
}
atom-notification .content::-webkit-scrollbar {
  display: none;
}
atom-notification .item {
  padding: 10px;
  border-top: 1px solid rgba(0, 0, 0, 0.1);
}
atom-notification .item.message {
  border-top: none;
}
atom-notification .item.message p:last-child {
  margin-bottom: 0;
}
atom-notification.has-close .message {
  padding-right: 34px;
}
atom-notification .close-all.btn {
  position: absolute;
  top: 6px;
  right: 38px;
  background: none;
}
atom-notification .close {
  position: absolute;
  top: 0;
  right: 0;
  width: 38px;
  height: 38px;
  font-size: 16px;
  text-shadow: none;
  color: inherit;
  opacity: .3;
}
atom-notification .close:hover,
atom-notification .close:focus {
  opacity: 1;
  color: inherit;
}
atom-notification .close:active {
  opacity: .2;
}
atom-notification .close:before {
  margin: 0;
  color: inherit;
}
atom-notification.has-detail {
  width: 450px;
}
atom-notification .detail {
  font-size: .8em;
  background-color: rgba(255, 255, 255, 0.3);
  background-clip: padding-box;
}
atom-notification .detail .line {
  white-space: pre;
  font-family: Consolas, "Liberation Mono", Menlo, Courier, monospace;
}
atom-notification .detail .stack-toggle {
  display: block;
  margin-top: 10px;
}
atom-notification .detail .stack-toggle .icon:before {
  margin: 0;
}
atom-notification .detail .stack-container {
  margin-top: 10px;
}
atom-notification .fatal-notification {
  font-size: .8em;
}
atom-notification .btn-toolbar {
  margin-top: 10px;
}
atom-notification.fatal {
  color: #000000;
  background-color: #ff4d4d;
}
atom-notification.fatal a {
  color: #660000;
}
atom-notification.fatal code {
  color: #000000;
  background-color: #fa2e2e;
}
atom-notification.fatal.icon:before {
  color: #ff8585;
  background-color: #cc0000;
}
atom-notification.fatal .close-all.btn {
  border: 1px solid rgba(0, 0, 0, 0.3);
  color: rgba(0, 0, 0, 0.6);
  text-shadow: none;
}
atom-notification.fatal .close-all.btn:hover {
  background: none;
  border-color: rgba(0, 0, 0, 0.8);
  color: #000000;
}
atom-notification.error {
  color: #000000;
  background-color: #ff4d4d;
}
atom-notification.error a {
  color: #660000;
}
atom-notification.error code {
  color: #000000;
  background-color: #fa2e2e;
}
atom-notification.error.icon:before {
  color: #ff8585;
  background-color: #cc0000;
}
atom-notification.error .close-all.btn {
  border: 1px solid rgba(0, 0, 0, 0.3);
  color: rgba(0, 0, 0, 0.6);
  text-shadow: none;
}
atom-notification.error .close-all.btn:hover {
  background: none;
  border-color: rgba(0, 0, 0, 0.8);
  color: #000000;
}
atom-notification.warning {
  color: #603100;
  background-color: #ffa380;
}
atom-notification.warning a {
  color: #c66500;
}
atom-notification.warning code {
  color: #603100;
  background-color: #fb8c60;
}
atom-notification.warning.icon:before {
  color: #ffccb8;
  background-color: #ff4800;
}
atom-notification.warning .close-all.btn {
  border: 1px solid rgba(96, 49, 0, 0.3);
  color: rgba(96, 49, 0, 0.6);
  text-shadow: none;
}
atom-notification.warning .close-all.btn:hover {
  background: none;
  border-color: rgba(96, 49, 0, 0.8);
  color: #603100;
}
atom-notification.info {
  color: #112e4d;
  background-color: #80cbff;
}
atom-notification.info a {
  color: #2460a0;
}
atom-notification.info code {
  color: #112e4d;
  background-color: #60bcfb;
}
atom-notification.info.icon:before {
  color: #b8e2ff;
  background-color: #0098ff;
}
atom-notification.info .close-all.btn {
  border: 1px solid rgba(17, 46, 77, 0.3);
  color: rgba(17, 46, 77, 0.6);
  text-shadow: none;
}
atom-notification.info .close-all.btn:hover {
  background: none;
  border-color: rgba(17, 46, 77, 0.8);
  color: #112e4d;
}
atom-notification.success {
  color: #000000;
  background-color: #72efa8;
}
atom-notification.success a {
  color: #294d17;
}
atom-notification.success code {
  color: #000000;
  background-color: #56e695;
}
atom-notification.success.icon:before {
  color: #a4f5c7;
  background-color: #17ca65;
}
atom-notification.success .close-all.btn {
  border: 1px solid rgba(0, 0, 0, 0.3);
  color: rgba(0, 0, 0, 0.6);
  text-shadow: none;
}
atom-notification.success .close-all.btn:hover {
  background: none;
  border-color: rgba(0, 0, 0, 0.8);
  color: #000000;
}
atom-notification {
  -webkit-animation: notification-show 0.16s cubic-bezier(0.175, 0.885, 0.32, 1.27499);
}
atom-notification[type="fatal"] {
  -webkit-animation: notification-show 0.16s cubic-bezier(0.175, 0.885, 0.32, 1.27499), notification-shake 4s 2s;
  -webkit-animation-iteration-count: 1, 3;
}
atom-notification[type="fatal"]:hover {
  -webkit-animation-play-state: paused;
}
atom-notification.remove,
atom-notification.remove:hover {
  -webkit-animation: notification-hide 0.24s cubic-bezier(0.33859, -0.42, 1, -0.22), notification-shrink 0.24s 0.24s cubic-bezier(0.5, 0, 0, 1);
  -webkit-animation-fill-mode: forwards;
}
@-webkit-keyframes notification-show {
  0% {
    opacity: 0;
    transform: perspective(450px) translate(0, -30px) rotateX(90deg);
  }
  100% {
    opacity: 1;
    transform: perspective(450px) translate(0, 0) rotateX(0deg);
  }
}
@-webkit-keyframes notification-hide {
  0% {
    opacity: 1;
    transform: scale(1);
  }
  100% {
    opacity: 0;
    transform: scale(0.8);
  }
}
@-webkit-keyframes notification-shrink {
  0% {
    opacity: 0;
    max-height: 800px;
    margin-bottom: 2px;
    transform: scale(0.8);
  }
  100% {
    opacity: 0;
    max-height: 0;
    margin-bottom: 0;
    transform: scale(0.8);
  }
}
@-webkit-keyframes notification-shake {
  0% {
    transform: translateX(0);
  }
  2% {
    transform: translateX(-4px);
  }
  4% {
    transform: translateX(8px);
  }
  6% {
    transform: translateX(-4px);
  }
  8% {
    transform: translateX(0);
  }
  100% {
    transform: translateX(0);
  }
}
