css:
.normal-mode-input atom-text-editor[mini] {
  background-color: inherit;
  border: none;
  width: 100%;
  font-weight: normal;
  color: #9da5b4;
  line-height: 1.28;
  cursor: default;
  white-space: nowrap;
  padding-left: 10px;
}
.vim-hidden-normal-mode-input {
  height: 0px !important;
  width: 0px !important;
  overflow: hidden !important;
  border: none !important;
  padding: 0 !important;
  display: block !important;
  position: fixed !important;
  top: -10px !important;
  left: -10px !important;
}
atom-text-editor.vim-mode.normal-mode::shadow .cursor,
atom-text-editor.vim-mode.operator-pending-mode::shadow .cursor,
atom-text-editor.vim-mode.visual-mode::shadow .cursor,
atom-text-editor.vim-mode.normal-mode .cursor,
atom-text-editor.vim-mode.operator-pending-mode .cursor,
atom-text-editor.vim-mode.visual-mode .cursor {
  border: 0;
  background-color: #528bff;
  visibility: hidden;
  opacity: 0.5;
}
atom-text-editor.vim-mode.normal-mode.is-focused::shadow .cursor,
atom-text-editor.vim-mode.operator-pending-mode.is-focused::shadow .cursor,
atom-text-editor.vim-mode.visual-mode.is-focused::shadow .cursor,
atom-text-editor.vim-mode.normal-mode.is-focused .cursor,
atom-text-editor.vim-mode.operator-pending-mode.is-focused .cursor,
atom-text-editor.vim-mode.visual-mode.is-focused .cursor {
  border: 0;
  background-color: #528bff;
  visibility: visible;
  opacity: 0.5;
}
atom-text-editor.vim-mode.visual-mode::shadow .cursor.hidden-cursor,
atom-text-editor.vim-mode.visual-mode .cursor.hidden-cursor {
  display: block;
}
atom-text-editor.vim-mode.replace-mode::shadow .cursor,
atom-text-editor.vim-mode.replace-mode .cursor {
  border: none;
  border-bottom: 0.3em solid #528bff;
  background: none;
  visibility: hidden;
  opacity: 0.5;
}
atom-text-editor.vim-mode.replace-mode.is-focused::shadow .cursor,
atom-text-editor.vim-mode.replace-mode.is-focused .cursor {
  border: none;
  border-bottom: 0.3em solid #528bff;
  background: none;
  visibility: visible;
  opacity: 0.5;
}
atom-text-editor.vim-mode.replace-mode.is-focused::shadow .cursors.blink-off .cursor,
atom-text-editor.vim-mode.replace-mode.is-focused .cursors.blink-off .cursor {
  border: none;
  border-bottom: 0.3em solid #528bff;
  background: none;
  visibility: hidden;
  opacity: 0.5;
}
atom-text-editor.vim-mode.operator-pending-mode::shadow .cursor,
atom-text-editor.vim-mode.operator-pending-mode .cursor {
  border: none;
  border-bottom: 0.6em solid #528bff;
  background: none;
  visibility: hidden;
  opacity: 0.5;
}
atom-text-editor.vim-mode.operator-pending-mode.is-focused::shadow .cursor,
atom-text-editor.vim-mode.operator-pending-mode.is-focused .cursor {
  border: none;
  border-bottom: 0.6em solid #528bff;
  background: none;
  visibility: visible;
  opacity: 0.5;
}
atom-text-editor.vim-mode.operator-pending-mode.is-focused::shadow .cursors.blink-off .cursor,
atom-text-editor.vim-mode.operator-pending-mode.is-focused .cursors.blink-off .cursor {
  border: none;
  border-bottom: 0.6em solid #528bff;
  background: none;
  visibility: hidden;
  opacity: 0.5;
}
.search-input atom-text-editor[mini]::before {
  content: '/';
}
.reverse-search-input atom-text-editor[mini]::before {
  content: '?';
}
