css:
.welcome {
  display: flex;
  font-size: 1.25em;
  line-height: 1.4;
  color: #444;
  background-color: #f4f4f4;
  overflow-x: auto;
}
.welcome p {
  line-height: inherit;
}
.welcome a {
  color: #5293d8;
}
.welcome ul {
  padding-left: 2em;
}
.welcome label {
  margin-top: 1em;
  font-weight: normal;
}
.welcome .input-checkbox {
  margin-top: -0.2em;
  margin-right: .5em;
}
.welcome-container {
  width: 100%;
  max-width: 580px;
  min-width: 300px;
  margin: auto;
  padding: 3em 2em;
}
.welcome-header {
  margin: 0 0 3em 0;
}
.welcome-logo {
  display: block;
  width: 100%;
  max-width: 280px;
  margin: 0 auto 2em auto;
  color: #000;
}
.welcome-title {
  font-size: 1.4em;
  text-align: center;
  line-height: 1.3;
  margin: 1em 0;
}
.welcome-card {
  margin: 1em 0;
  border-radius: 4px;
  border: 1px solid #9f9f9f;
  background-color: #fcfcfc;
}
.welcome-summary {
  padding: 1em 1.5em;
  font-size: 1.1em;
  font-weight: 300;
  line-height: 1.2;
  cursor: pointer;
}
.welcome-summary:hover {
  color: #4e4e4e;
}
.welcome-summary::-webkit-details-marker {
  display: none;
}
.welcome-summary:before {
  width: 20px;
  color: #999;
}
details[open] .welcome-summary:before {
  color: #000;
}
details[open] .welcome-summary {
  color: #000;
}
.welcome-highlight {
  color: #000;
}
.welcome-detail {
  border-top: 1px solid #9f9f9f;
  padding: 1.5em;
}
.welcome-detail .welcome-note {
  margin-bottom: 0;
}
.welcome-img {
  display: block;
  max-width: 100%;
  border-radius: 3px;
  margin: 0 0 1em 0;
  border: 1px solid #9f9f9f;
}
.welcome-consent {
  margin-top: 3em;
}
.welcome-consent-choices {
  display: flex;
  flex-direction: row;
  flex-wrap: wrap;
  margin: 1em -1em;
}
.welcome-consent-choices button {
  width: 100%;
}
.welcome-consent-choices div {
  margin: .5em 1em;
  flex: 1;
}
.welcome-consent-choices .welcome-note {
  margin-top: 1em;
}
.welcome-key {
  display: inline-block;
  padding: .3em .4em;
  font-size: .8em;
  line-height: 1;
  border-radius: 3px;
  color: #000;
  border: 1px solid #999;
  background: rgba(0, 0, 0, 0.1);
}
.welcome-love {
  color: #999;
}
.welcome-love a {
  color: #999;
}
.welcome-love .icon::before {
  position: relative;
  top: 1px;
  width: auto;
  margin-right: 0;
  font-size: 1.5em;
}
.welcome-love .icon-logo-github::before {
  top: 3px;
}
.welcome-note {
  font-size: .86em;
  color: #999;
}
.welcome-footer {
  margin-top: 1em;
  text-align: center;
}
